parameters.comments:
remoteHosts:
      - id: ext-lj-1
        host: "external-logstash-1"
        port: 5044
        codec: json
        inclusions:
          - field: "[facility]"
            value: "log audit"
          - field: "[metadata][category]"
            pattern: 'privacy*'
        exclusions:
          - logplane:
            rules:
              - field: "[facility]"
                value: "security"
              - field: "[severity]"
                contains: "error"
      - id: ext-lj-2
        host: "external-logstash-2"
        port: 5044
        codec: json
        filter: |
          mutate {
                add_field => { "[metadata][applicationId]" => testApplicationId }
                add_field => { "[kubernetes.node.name]" => "testnode" }
          }
          if [extra_data][asi][log_plane] == "alarm" {
                mutate {
                  replace => { "logplane" => "adp-app-asi-logs" }
                }
          }
          if [metadata][category] == "in-service performance" or [metadata][category] == "HA-in-service-performance" {
            mutate {
              replace => { "logplane" => "isp-logs" }
            }
          }
          if [facility] == "log audit" or "privacy" in [facility] {
            mutate {
              replace => { "logplane" => "adp-app-audit-logs" }
            }
          }
          if [logplane] != "adp-app-audit-logs" and [logplane] != "isp-logs" and [logplane] != "adp-app-asi-logs" {
            drop{}
          }
          mutate {
                gsub => [
                  "message", "\"","""
                ]
          }
